import React, { useState } from "react";
import "primereact/resources/primereact.min.css";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import "primeicons/primeicons.css";
import $ from "jquery";
import "primeflex/primeflex.css";
import "primereact/resources/themes/vela-blue/theme.css";
import { InputText } from "primereact/inputtext";
const jsdom = require("jsdom");
const { JSDOM } = jsdom;

// import IframeResizer from "iframe-resizer-react";

let temp =
  "https://pubchem.ncbi.nlm.nih.gov/compound/Benzene#section=Structures&embed=true&hide_title=true";

const SearchBar = () => {
  const [value, setValue] = useState("Benzene");
  // const [widgetId, setWidgetId] = useState(temp);
  const [child, setChild] = useState(`<marquee>Hello</marquee>`);

  let result = () => {
    fetch(temp)
      .then((o) => {
        return o.text();
      })
      .then((o) => {
        const result = o.replace(/nomodule/gm, "noModule");
        const dom = new JSDOM(result);
        let body = dom.window.document.body.innerHTML;
        let style = dom.window.document.querySelector("style").innerHTML;
        // let HtmlToReactParser = require("html-to-react").Parser;
        // let htmlToReactParser = new HtmlToReactParser();
        // let reactEl = htmlToReactParser.parse(
        //   "<style>" + style + "</style>" + body
        // );
        let reactEl = $.parseHTML("<style>" + style + "</style>" + body);
        setChild(reactEl);
      });
  };

  return (
    <div>
      <div className="p-card">
        <span className="p-input-icon-left p-card-content">
          <i className="pi pi-search" />
          <InputText
            style={{ width: "800px" }}
            value={value}
            onChange={(e) => {
              setValue(e.target.value);
              temp =
                "https://pubchem.ncbi.nlm.nih.gov/compound/" +
                e.target.value +
                "#section=Structures&embed=true&hide_title=true";
            }}
            onKeyPress={(e) => {
              if (e.code === "Enter") {
                // setWidgetId(temp);
                result();
              }
            }}
            placeholder="Type Here To Search"
          />
          <Button
            style={{ marginLeft: "-38px" }}
            icon="pi pi-search"
            className="p-button-rounded"
            onClick={() => {
              // setWidgetId(temp);
              result();
            }}
          />
        </span>
      </div>
      <div style={{ padding: 100 }}>
        <Card
          title=""
          style={{ width: "75rem", margin: "auto", borderRadius: "10px" }}
        >
          {/* <IframeResizer
            title="Structures"
            id="struct"
            heightCalculationMethod="bodyScroll"
            className="pubchem-widget"
            target="_self"
            src={widgetId}
            checkOrigin={false}
            style={{
              width: "100%",
              minHeight: 600,
              border: "0px",
              borderRadius: "10px",
            }}
          ></IframeResizer> */}
        </Card>
        <section>{child}</section>
      </div>
    </div>
  );
};

export default SearchBar;
