import React, { useState } from "react";
import "primereact/resources/primereact.min.css";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import "primeicons/primeicons.css";
import IframeResizer from "iframe-resizer-react";
import "primeflex/primeflex.css";
import "primereact/resources/themes/saga-purple/theme.css";
import { InputText } from "primereact/inputtext";
import PeriodicTable from "./PeriodicTable";
import { type } from "jquery";

let temp =
  "https://pubchem.ncbi.nlm.nih.gov/compound/Benzene#section=Structures&embed=true&hide_title=true";

const SearchBar = () => {
  const [value, setValue] = useState("Benzene");
  const [widgetId, setWidgetId] = useState(temp);
  console.log(getAtoms(value));

  return (
    <div>
      <div className="p-card" style={{ minWidth: "200px" }}>
        <span className="p-input-icon-left p-card-content">
          <i className="pi pi-search" />
          <InputText
            style={{ maxWidth: "800px", width: "600px" }}
            value={value}
            onChange={(e) => {
              setValue(e.target.value);
              temp =
                "https://pubchem.ncbi.nlm.nih.gov/compound/" +
                e.target.value +
                "#section=Structures&embed=true&hide_title=true";
            }}
            onKeyPress={(e) => {
              if (e.code === "Enter") {
                setWidgetId(temp);
              }
            }}
            placeholder="Type Here To Search"
          />
          <Button
            style={{ marginLeft: "-38px" }}
            icon="pi pi-search"
            className="p-button"
            onClick={() => {
              setWidgetId(temp);
            }}
          />
        </span>
      </div>
      <div style={{ paddingTop: 100 }}>
        <Card
          title=""
          style={{ width: "75rem", margin: "auto", borderRadius: "10px" }}
        >
          <IframeResizer
            title="Structures"
            id="struct"
            heightCalculationMethod="bodyScroll"
            className="pubchem-widget"
            target="_self"
            src={widgetId}
            checkOrigin={false}
            style={{
              width: "100%",
              minHeight: 600,
              border: "0px",
              borderRadius: "10px",
            }}
          ></IframeResizer>
          <PeriodicTable toShow={{ H: 6, C: 6 }} compound={value} />
        </Card>
      </div>
    </div>
  );
};

let getAtoms = (name) => {
  const url = `https://pubchem.ncbi.nlm.nih.gov/rest/pug/compound/name/${name}/property/MolecularFormula/JSON`;
  return fetch(url)
    .then((resp) => {
      return resp.json();
    })
    .then((data) => {
      return data["PropertyTable"]["Properties"][0]["MolecularFormula"];
    })
    .then((compound) => {
      const regex = /[a-zA-Z]+\d+/gm;
      const atoms = {};
      let results;
      while ((results = regex.exec(compound)) !== null) {
        if (results.index === regex.lastIndex) {
          regex.lastIndex++;
        }
        let symbolAndCount = (match) => {
          const regSym = /[a-zA-Z]+/gm;
          const regNum = /\d+/gm;
          let Sym = String(regSym.exec(match)[0]);
          let Num = Number(regNum.exec(match)[0]);
          return [Sym, Num];
        };
        let info = symbolAndCount(results);
        atoms[symbol] = count;
      }
      return atoms;
    });
};

export default SearchBar;
